// src/pages/DashboardPage.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';

function DashboardPage() {
  const [data, setData] = useState('');
  const [command, setCommand] = useState('');
  const socket = io('http://localhost:3001');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:3001/api/protected', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setData(response.data);
      } catch (error) {
        console.error('Error fetching protected data', error);
      }
    };

    fetchData();

    return () => {
      socket.disconnect();
    };
  }, [socket]);

  const handleCommandSend = () => {
    socket.emit('control-command', command);
    setCommand('');
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center">Dashboard</h1>
        <p className="mb-4 text-center">{data}</p>
        <div className="mt-4">
          <input
            type="text"
            value={command}
            onChange={(e) => setCommand(e.target.value)}
            placeholder="Enter command"
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm p-2"
          />
          <button
            onClick={handleCommandSend}
            className="w-full bg-indigo-600 text-white p-2 rounded-md hover:bg-indigo-700 transition duration-300 mt-2"
          >
            Send Command
          </button>
        </div>
      </div>
    </div>
  );
}

export default DashboardPage;
