// client/src/components/RemoteControl.js
import React, { useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

const RemoteControl = () => {
  const [sessionId, setSessionId] = useState('');
  const [connectedSession, setConnectedSession] = useState('');
  const [command, setCommand] = useState('');

  const createSession = () => {
    socket.emit('createSession');
    socket.on('sessionCreated', (id) => {
      setSessionId(id);
    });
  };

  const joinSession = () => {
    socket.emit('joinSession', sessionId);
    socket.on('sessionJoined', (id) => {
      setConnectedSession(id);
    });
  };

  const sendCommand = () => {
    socket.emit('sendCommand', { sessionId: connectedSession, command });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-8">Remote Control</h1>
      <div className="mb-4">
        <button
          onClick={createSession}
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Create Session
        </button>
      </div>
      <div className="mb-4">
        <input
          type="text"
          value={sessionId}
          onChange={(e) => setSessionId(e.target.value)}
          placeholder="Session ID"
          className="border-2 border-gray-300 p-2 rounded"
        />
        <button
          onClick={joinSession}
          className="bg-green-500 text-white py-2 px-4 ml-2 rounded hover:bg-green-700"
        >
          Join Session
        </button>
      </div>
      <div>
        <input
          type="text"
          value={command}
          onChange={(e) => setCommand(e.target.value)}
          placeholder="Command"
          className="border-2 border-gray-300 p-2 rounded"
        />
        <button
          onClick={sendCommand}
          className="bg-red-500 text-white py-2 px-4 ml-2 rounded hover:bg-red-700"
        >
          Send Command
        </button>
      </div>
    </div>
  );
};

export default RemoteControl;
