// src/pages/DashboardPage.js

import React, { useState } from 'react';

function DashboardPage() {
  const [devices, setDevices] = useState([
    { id: 1, name: 'Device 1', status: 'off' },
    { id: 2, name: 'Device 2', status: 'on' },
    { id: 3, name: 'Device 3', status: 'off' },
  ]);

  const toggleDevice = (id) => {
    setDevices(devices.map(device =>
      device.id === id ? { ...device, status: device.status === 'on' ? 'off' : 'on' } : device
    ));
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Dashboard</h1>
      <ul>
        {devices.map(device => (
          <li key={device.id} className="mb-2 flex justify-between items-center">
            <span>{device.name}</span>
            <button
              onClick={() => toggleDevice(device.id)}
              className={`p-2 rounded-md ${device.status === 'on' ? 'bg-green-500' : 'bg-red-500'}`}
            >
              {device.status === 'on' ? 'Turn Off' : 'Turn On'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DashboardPage;
