import React, { useEffect, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'peerjs';

const RemoteControl = () => {
  const socket = useRef(null);
  const peer = useRef(null);
  const videoRef = useRef(null);

  useEffect(() => {
    socket.current = io('http://localhost:5000');

    peer.current = new Peer(undefined, {
      host: 'localhost',
      port: 5000,
      path: '/peerjs',
    });

    peer.current.on('open', (id) => {
      console.log('Peer ID:', id);
    });

    peer.current.on('call', (call) => {
      call.answer();
      call.on('stream', (stream) => {
        videoRef.current.srcObject = stream;
      });
    });

    socket.current.on('mouseMove', (data) => {
      // Send mouse movement data
      socket.current.emit('mouseMove', data);
    });

    socket.current.on('mouseClick', (data) => {
      // Send mouse click data
      socket.current.emit('mouseClick', data);
    });

    socket.current.on('keyPress', (data) => {
      // Send key press data
      socket.current.emit('keyPress', data);
    });
  }, []);

  const handleMouseMove = (e) => {
    const data = { x: e.clientX, y: e.clientY };
    socket.current.emit('mouseMove', data);
  };

  const handleMouseClick = (e) => {
    const data = { button: e.button };
    socket.current.emit('mouseClick', data);
  };

  const handleKeyPress = (e) => {
    const data = { key: e.key };
    socket.current.emit('keyPress', data);
  };

  return (
    <div
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
      tabIndex="0"
      onKeyDown={handleKeyPress}
      style={{ width: '100%', height: '100vh', backgroundColor: 'lightgray' }}
    >
      <video ref={videoRef} autoPlay style={{ width: '100%' }}></video>
    </div>
  );
};

export default RemoteControl;
