// src/pages/DashboardPage.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

function DashboardPage() {
  const [devices, setDevices] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
    }

    axios.get('http://localhost:3001/api/dashboard', {
      headers: {
        Authorization: token,
      },
    }).then(response => {
      // هنا يمكننا الحصول على الأجهزة المتصلة للمستخدم
      setDevices([
        { id: 1, name: 'Office PC' },
        { id: 2, name: 'Home Laptop' },
      ]);
    }).catch(error => {
      console.error('Error fetching dashboard data', error);
    });
  }, [navigate]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <h1 className="text-3xl font-bold mb-6">Dashboard</h1>
      <div className="w-full max-w-md bg-white p-4 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4">Your Devices</h2>
        <ul>
          {devices.map(device => (
            <li key={device.id} className="mb-2">
              <Link to={`/device/${device.id}`} className="text-blue-500 hover:underline">
                {device.name}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default DashboardPage;
