import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';

const SERVER_URL = 'http://192.168.1.x:4000'; // ضع عنوان IP المحلي للحاسب
const socket = io(SERVER_URL, { transports: ['websocket', 'polling'] });

const RemoteControl = () => {
  const [sessionId, setSessionId] = useState('');
  const [connectedSession, setConnectedSession] = useState('');
  const [stream, setStream] = useState(null);
  const [isSharing, setIsSharing] = useState(false);
  const videoRef = useRef();
  const peerRef = useRef();

  useEffect(() => {
    socket.on('connect', () => {
      console.log('Connected to socket server');
    });

    socket.on('disconnect', () => {
      console.log('Disconnected from socket server');
    });

    socket.on('receiveMessage', (message) => {
      console.log('Message received:', message);
      alert(`Message received: ${message}`);
    });

    socket.on('shareRequest', () => {
      const accept = window.confirm('Do you want to share your screen?');
      socket.emit('shareResponse', { sessionId: connectedSession, response: accept });
    });

    socket.on('shareResponse', (response) => {
      if (response) {
        startSharing();
      } else {
        alert('Sharing request was denied.');
      }
    });

    socket.on('receiveSignal', (signal) => {
      if (peerRef.current) {
        peerRef.current.signal(signal);
      }
    });
  }, [connectedSession]);

  const createSession = () => {
    console.log('Create session button clicked');
    socket.emit('createSession');
    socket.on('sessionCreated', (id) => {
      console.log('Session created with ID:', id);
      setSessionId(id);
    });
  };

  const joinSession = () => {
    console.log('Join session button clicked');
    console.log('Attempting to join session with ID:', sessionId);
    socket.emit('joinSession', sessionId);
    socket.on('sessionJoined', (id) => {
      console.log('Joined session with ID:', id);
      setConnectedSession(id);
      socket.emit('requestShare', id);
    });
  };

  const startSharing = () => {
    navigator.mediaDevices.getDisplayMedia({ cursor: true }).then((stream) => {
      setStream(stream);
      videoRef.current.srcObject = stream;
      const peer = new Peer({
        initiator: true,
        trickle: false,
        stream: stream
      });
      peerRef.current = peer;

      peer.on('signal', (signal) => {
        socket.emit('sendSignal', { sessionId: connectedSession, signal });
      });

      peer.on('stream', (remoteStream) => {
        videoRef.current.srcObject = remoteStream;
      });
    }).catch((error) => {
      console.error('Error accessing display media:', error);
      alert('Permission denied for screen sharing. Please allow screen sharing permission and try again.');
    });
  };

  const joinSharing = () => {
    console.log('Join sharing button clicked');
    const peer = new Peer({
      initiator: false,
      trickle: false
    });
    peerRef.current = peer;

    peer.on('signal', (signal) => {
      socket.emit('sendSignal', { sessionId: sessionId, signal });
    });

    peer.on('stream', (remoteStream) => {
      videoRef.current.srcObject = remoteStream;
    });
  };

  const sendMessage = () => {
    const message = "مرحبا";
    socket.emit('sendMessage', { sessionId: connectedSession || sessionId, message });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-8">Remote Control</h1>
      <div className="mb-4">
        <button
          onClick={createSession}
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Create Session
        </button>
      </div>
      <div className="mb-4">
        <input
          type="text"
          value={sessionId}
          onChange={(e) => setSessionId(e.target.value)}
          placeholder="Session ID"
          className="border-2 border-gray-300 p-2 rounded"
        />
        <button
          onClick={joinSession}
          className="bg-green-500 text-white py-2 px-4 ml-2 rounded hover:bg-green-700"
        >
          Join Session
        </button>
      </div>
      <div className="mb-4">
        <button
          onClick={joinSharing}
          className="bg-yellow-500 text-white py-2 px-4 rounded hover:bg-yellow-700"
        >
          Join Sharing
        </button>
      </div>
      <div className="mb-4">
        <button
          onClick={sendMessage}
          className="bg-purple-500 text-white py-2 px-4 rounded hover:bg-purple-700"
        >
          Send "مرحبا"
        </button>
      </div>
      <video ref={videoRef} autoPlay className="border-2 border-gray-300 rounded"></video>
    </div>
  );
};

export default RemoteControl;
