// src/pages/DeviceControlPage.js

import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import SimplePeer from 'simple-peer';
import io from 'socket.io-client';

const socket = io('http://localhost:3002');

function DeviceControlPage() {
  const { id } = useParams();
  const [peer, setPeer] = useState(null);
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();

  useEffect(() => {
    socket.on('signal', data => {
      if (peer) {
        peer.signal(data);
      }
    });

    return () => {
      socket.off('signal');
    };
  }, [peer]);

  const startRemoteControl = () => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      localVideoRef.current.srcObject = stream;

      const newPeer = new SimplePeer({
        initiator: window.location.hash === `#init`,
        trickle: false,
        stream,
      });

      newPeer.on('signal', data => {
        socket.emit('signal', data);
      });

      newPeer.on('stream', stream => {
        remoteVideoRef.current.srcObject = stream;
      });

      setPeer(newPeer);
    }).catch(err => {
      console.error('Failed to get local stream', err);
    });
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <h1 className="text-3xl font-bold mb-6">Control Device</h1>
      <div className="w-full max-w-md bg-white p-4 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4">Device ID: {id}</h2>
        <div className="mt-4">
          <button
            onClick={startRemoteControl}
            className="w-full bg-indigo-600 text-white p-2 rounded-md hover:bg-indigo-700 transition duration-300"
          >
            Start Remote Control
          </button>
        </div>
        <div className="mt-4">
          <video ref={localVideoRef} autoPlay playsInline className="w-full mb-4"></video>
          <video ref={remoteVideoRef} autoPlay playsInline className="w-full"></video>
        </div>
      </div>
    </div>
  );
}

export default DeviceControlPage;
