// src/pages/ControlDevicePage.js
import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import { useParams } from 'react-router-dom';

const socket = io('http://localhost:3002');

const ControlDevicePage = () => {
  const { sessionCode } = useParams();
  const [output, setOutput] = useState('');

  useEffect(() => {
    socket.emit('join-session', sessionCode);

    socket.on('session-joined', () => {
      setOutput(`Joined session: ${sessionCode}\n`);
    });

    socket.on('output', (data) => {
      setOutput((prev) => prev + data);
    });

    return () => {
      socket.off('session-joined');
      socket.off('output');
    };
  }, [sessionCode]);

  const handleInput = (e) => {
    if (e.key === 'Enter') {
      socket.emit('send-key', e.target.value);
      e.target.value = '';
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <div className="max-w-4xl w-full p-6 bg-white shadow-lg rounded-lg">
        <h2 className="text-2xl font-bold mb-4">Controlling Device</h2>
        <div className="bg-black text-white p-4 rounded-md mb-4" style={{ height: '300px', overflowY: 'scroll' }}>
          <pre>{output}</pre>
        </div>
        <input
          type="text"
          onKeyDown={handleInput}
          className="w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
          placeholder="Type command here"
        />
      </div>
    </div>
  );
};

export default ControlDevicePage;
