// src/pages/EnterSessionCodePage.js
import React, { useState } from 'react';
import io from 'socket.io-client';
import { useNavigate } from 'react-router-dom';

const socket = io('http://localhost:3002');

const EnterSessionCodePage = () => {
  const [sessionCode, setSessionCode] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setSessionCode(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (sessionCode) {
      socket.emit('join-session', sessionCode);
      navigate(`/control-device/${sessionCode}`);
    } else {
      setError('Please enter a valid session code');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="max-w-md mx-auto p-6 bg-white shadow-lg rounded-lg">
        <h2 className="text-2xl font-bold mb-4">Enter Session Code</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="sessionCode" className="block text-sm font-medium text-gray-700">Session Code</label>
            <input
              type="text"
              id="sessionCode"
              name="sessionCode"
              value={sessionCode}
              onChange={handleChange}
              className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
            {error && <p className="text-red-500 text-sm">{error}</p>}
          </div>
          <button
            type="submit"
            className="w-full bg-indigo-600 text-white py-2 rounded-md hover:bg-indigo-700 transition duration-300"
          >
            Connect
          </button>
        </form>
      </div>
    </div>
  );
};

export default EnterSessionCodePage;
