import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';

const SERVER_URL = 'http://192.168.1.8:4000'; // استبدل بـ IP المحلي للحاسب
const socket = io(SERVER_URL);

const App = () => {
  const [sessionId, setSessionId] = useState('');
  const [inputSessionId, setInputSessionId] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [shareDenied, setShareDenied] = useState(false);
  const videoRef = useRef();
  const peerRef = useRef();

  useEffect(() => {
    socket.on('connect', () => {
      console.log('Connected to socket server');
    });

    socket.on('disconnect', () => {
      console.log('Disconnected from socket server');
    });

    socket.on('joinRequest', ({ id, sessionId }) => {
      const accept = window.confirm('Do you want to share your screen?');
      socket.emit('shareResponse', { sessionId, response: accept, id });
      if (accept) {
        setIsConnected(true);
      } else {
        setShareDenied(true);
      }
    });

    socket.on('shareResponse', (response) => {
      if (response) {
        startSharing();
      } else {
        alert('Sharing request was denied.');
      }
    });

    socket.on('receiveSignal', (signal) => {
      if (peerRef.current) {
        peerRef.current.signal(signal);
      }
    });
  }, []);

  const createSession = () => {
    socket.emit('createSession');
    socket.on('sessionCreated', (id) => {
      setSessionId(id);
    });
  };

  const joinSession = () => {
    socket.emit('joinSession', inputSessionId);
    setIsConnected(true);
  };

  const startSharing = () => {
    navigator.mediaDevices.getDisplayMedia({ cursor: true }).then((stream) => {
      videoRef.current.srcObject = stream;
      const peer = new Peer({
        initiator: true,
        trickle: false,
        stream: stream
      });
      peerRef.current = peer;

      peer.on('signal', (signal) => {
        socket.emit('sendSignal', { sessionId: inputSessionId, signal });
      });

      peer.on('stream', (remoteStream) => {
        videoRef.current.srcObject = remoteStream;
      });
    }).catch((error) => {
      console.error('Error accessing display media:', error);
      alert('Permission denied for screen sharing. Please allow screen sharing permission and try again.');
    });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-8">Remote Control</h1>
      <div className="mb-4">
        <button
          onClick={createSession}
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Create Session
        </button>
        {sessionId && <p className="mt-2 text-lg">Session ID: {sessionId}</p>}
      </div>
      <div className="mb-4 flex">
        <input
          type="text"
          value={inputSessionId}
          onChange={(e) => setInputSessionId(e.target.value)}
          placeholder="Enter Session ID"
          className="border-2 border-gray-300 p-2 rounded mr-2"
        />
        <button
          onClick={joinSession}
          className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-700"
        >
          Join Session
        </button>
      </div>
      {shareDenied && <p className="text-red-500">Sharing request was denied.</p>}
      <video
        ref={videoRef}
        autoPlay
        className="border-2 border-gray-300 rounded w-full max-w-md mt-4"
      ></video>
    </div>
  );
};

export default App;
