// src/pages/DeviceControlPage.js

import React, { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import SimplePeer from 'simple-peer';
import io from 'socket.io-client';

const socket = io('http://localhost:3002');

function DeviceControlPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [peer, setPeer] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const fileInputRef = useRef();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    socket.on('signal', data => {
      if (peer) {
        peer.signal(data);
      }
    });

    socket.on('message', message => {
      setMessages(prevMessages => [...prevMessages, message]);
    });

    return () => {
      socket.off('signal');
      socket.off('message');
    };
  }, [peer, navigate]);

  const startRemoteControl = () => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      localVideoRef.current.srcObject = stream;

      const newPeer = new SimplePeer({
        initiator: window.location.hash === `#init`,
        trickle: false,
        stream,
      });

      newPeer.on('signal', data => {
        socket.emit('signal', data);
      });

      newPeer.on('stream', stream => {
        remoteVideoRef.current.srcObject = stream;
      });

      newPeer.on('data', data => {
        if (data instanceof ArrayBuffer) {
          const fileBlob = new Blob([data]);
          const url = URL.createObjectURL(fileBlob);
          const a = document.createElement('a');
          a.href = url;
          a.download = 'remote-file';
          a.click();
        } else {
          setMessages(prevMessages => [...prevMessages, data.toString()]);
        }
      });

      setPeer(newPeer);
    }).catch(err => {
      console.error('Failed to get local stream', err);
    });
  };

  const sendMessage = () => {
    if (peer && newMessage) {
      peer.send(newMessage);
      setMessages(prevMessages => [...prevMessages, newMessage]);
      setNewMessage('');
    }
  };

  const sendFile = () => {
    const file = fileInputRef.current.files[0];
    if (peer && file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        peer.send(e.target.result);
      };
      reader.readAsArrayBuffer(file);
    }
  };

  const handleMouseMove = (e) => {
    const x = e.clientX;
    const y = e.clientY;
    socket.emit('mouse-move', x, y);
  };

  const handleMouseClick = () => {
    socket.emit('mouse-click');
  };

  const handleKeyTap = (key) => {
    socket.emit('key-tap', key);
  };

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center bg-gray-100"
      onMouseMove={handleMouseMove}
      onClick={handleMouseClick}
      onKeyDown={(e) => handleKeyTap(e.key)}
      tabIndex="0"
    >
      <h1 className="text-3xl font-bold mb-6">Control Device</h1>
      <div className="w-full max-w-md bg-white p-4 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4">Device ID: {id}</h2>
        <div className="mt-4">
          <button
            onClick={startRemoteControl}
            className="w-full bg-indigo-600 text-white p-2 rounded-md hover:bg-indigo-700 transition duration-300"
          >
            Start Remote Control
          </button>
        </div>
        <div className="mt-4">
          <video ref={localVideoRef} autoPlay playsInline className="w-full mb-4"></video>
          <video ref={remoteVideoRef} autoPlay playsInline className="w-full"></video>
        </div>
        <div className="mt-4">
          <h3 className="text-lg font-semibold mb-2">Messages</h3>
          <div className="bg-gray-100 p-2 rounded-lg mb-4 max-h-32 overflow-y-auto">
            {messages.map((msg, index) => (
              <div key={index} className="text-sm">{msg}</div>
            ))}
          </div>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message"
            className="p-2 border rounded-md w-full mb-2"
          />
          <button
            onClick={sendMessage}
            className="w-full bg-indigo-600 text-white p-2 rounded-md hover:bg-indigo-700 transition duration-300"
          >
            Send
          </button>
        </div>
        <div className="mt-4">
          <h3 className="text-lg font-semibold mb-2">Files</h3>
          <input
            type="file"
            ref={fileInputRef}
            className="p-2 border rounded-md w-full mb-2"
          />
          <button
            onClick={sendFile}
            className="w-full bg-indigo-600 text-white p-2 rounded-md hover:bg-indigo-700 transition duration-300"
          >
            Send File
          </button>
        </div>
      </div>
    </div>
  );
}

export default DeviceControlPage;
