import React, { useState, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';

const socket = io('http://localhost:5000');

const RemoteControl = () => {
  const [sessionId, setSessionId] = useState('');
  const [connectedSession, setConnectedSession] = useState('');
  const [stream, setStream] = useState(null);
  const videoRef = useRef();
  const peerRef = useRef();

  const createSession = () => {
    socket.emit('createSession');
    socket.on('sessionCreated', (id) => {
      setSessionId(id);
    });
  };

  const joinSession = () => {
    socket.emit('joinSession', sessionId);
    socket.on('sessionJoined', (id) => {
      setConnectedSession(id);
      startSharing();
    });
  };

  const startSharing = () => {
    navigator.mediaDevices.getDisplayMedia({ cursor: true }).then((stream) => {
      setStream(stream);
      videoRef.current.srcObject = stream;
      const peer = new Peer({
        initiator: true,
        trickle: false,
        stream: stream
      });
      peerRef.current = peer;

      peer.on('signal', (signal) => {
        socket.emit('sendSignal', { sessionId: connectedSession, signal });
      });

      socket.on('receiveSignal', (signal) => {
        peer.signal(signal);
      });

      peer.on('stream', (remoteStream) => {
        videoRef.current.srcObject = remoteStream;
      });
    });
  };

  const joinSharing = () => {
    const peer = new Peer({
      initiator: false,
      trickle: false
    });
    peerRef.current = peer;

    peer.on('signal', (signal) => {
      socket.emit('sendSignal', { sessionId: sessionId, signal });
    });

    socket.on('receiveSignal', (signal) => {
      peer.signal(signal);
    });

    peer.on('stream', (remoteStream) => {
      videoRef.current.srcObject = remoteStream;
    });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-8">Remote Control</h1>
      <div className="mb-4">
        <button
          onClick={createSession}
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Create Session
        </button>
      </div>
      <div className="mb-4">
        <input
          type="text"
          value={sessionId}
          onChange={(e) => setSessionId(e.target.value)}
          placeholder="Session ID"
          className="border-2 border-gray-300 p-2 rounded"
        />
        <button
          onClick={joinSession}
          className="bg-green-500 text-white py-2 px-4 ml-2 rounded hover:bg-green-700"
        >
          Join Session
        </button>
      </div>
      <div className="mb-4">
        <button
          onClick={joinSharing}
          className="bg-yellow-500 text-white py-2 px-4 rounded hover:bg-yellow-700"
        >
          Join Sharing
        </button>
      </div>
      <video ref={videoRef} autoPlay className="border-2 border-gray-300 rounded"></video>
    </div>
  );
};

export default RemoteControl;
