// src/pages/DashboardPage.js

import React from 'react';
import { Link } from 'react-router-dom';

function DashboardPage() {
  // هنا يمكن استدعاء API للحصول على قائمة الأجهزة المتصلة
  const devices = [
    { id: 1, name: 'Office PC' },
    { id: 2, name: 'Home Laptop' },
  ];

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <h1 className="text-3xl font-bold mb-6">Dashboard</h1>
      <div className="w-full max-w-md bg-white p-4 rounded-lg shadow-lg">
        <h2 className="text-2xl font-semibold mb-4">Your Devices</h2>
        <ul>
          {devices.map(device => (
            <li key={device.id} className="mb-2">
              <Link to={`/device/${device.id}`} className="text-blue-500 hover:underline">
                {device.name}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default DashboardPage;
